function addClass(e,s){new RegExp("(^|\\s)"+s+"(\\s|$)","g").test(e.className)||(e.className=(e.className+" "+s).replace(/\s+/g," ").replace(/(^ | $)/g,""))}function removeClass(e,s){var a=new RegExp("(^|\\s)"+s+"(\\s|$)","g");e.className=e.className.replace(a,"$1").replace(/\s+/g," ").replace(/(^ | $)/g,"")}var item=document.getElementById("brand");function active(){removeClass(this,"reset-brand"),addClass(this,"active-brand")}function reset(){removeClass(this,"active-brand"),addClass(this,"reset-brand")}item.addEventListener("mouseover",active,!1),item.addEventListener("mouseout",reset,!1);